        function numberFormat(numero) {
            // Variable que contendra el resultado final
            var resultado = "";
            var nuevoNumero = numero;
            // Si tiene decimales, se los quitamos al numero
            if (numero.indexOf(".") >= 0)
                nuevoNumero = numero.substring(0, numero.indexOf("."));

            // Ponemos un punto cada 3 caracteres
            for (j, i = nuevoNumero.length - 1, j = 0; i >= 0; i-- , j++)
                resultado = nuevoNumero.charAt(i) + ((j > 0) && (j % 3 == 0) ? "." : "") + resultado;

            // Si tiene decimales, se lo aadimos al numero una vez forateado con
            // los separadores de miles
            if (numero.indexOf(".") >= 0)
                resultado += "," + numero.substring(numero.indexOf(".") + 1);

            if (numero[0] == "-") {
                // Devolvemos el valor aadiendo al inicio el signo negativo
                return "-" + resultado;
            } else {
                return resultado;
            }
        }


